def find_seed(f,c=0,eps=2**-26):

    def g(x,y):
        return f(x,y)-c
    a=0
    b=1
    if g(0,0)*g(0,1)>0:
        return None

    while b-a>=eps:
        mil=(a+b)/2
        if g(0,a)*g(0,mil)<=0:
            b=mil
        else : 
            a=mil
    return a

